import 'rxjs';
import { Observable } from 'rxjs/Observable';
import { combineEpics } from 'redux-observable';
import { getCart } from '../reducers';
import { api } from '../services';
import * as actions from '../actions';

/**
 * search products epic
 * @param action$
 * @param store
 * @returns {any|*|Observable}
 */
const searchProducts = (action$) => {
  return action$
    .ofType(actions.GET_ALL_PRODUCTS).switchMap(q => {
      /*
       * This example uses the same api of the redux-saga example, thus
        * it adapts a Promise to an Observable.
       */
      return api.getProducts().map(actions.receiveProducts)
    });
};

/**
 * checkout epic.
 * @param action$
 * @param store
 * @returns {any|*|Observable}
 */
const checkout = (action$,store) => {
  return action$
    .ofType(actions.CHECKOUT_REQUEST).switchMap(q => {
      const cart = getCart(store.getState());
      return api.buyProducts(cart)
        .map(cart => actions.checkoutSuccess(cart))
        .catch(error => Observable.of(actions.checkoutFailure(error)))
    });
};


export const rootEpic = combineEpics(
  searchProducts,
  checkout
);

import * as promiseMiddleware from 'redux-promise';
import { createEpicMiddleware } from 'redux-observable';
import {LoggerMiddleware} from './middleware/logger.middleware';
import {Epics} from './root-epic';

const epicMiddleware = createEpicMiddleware(Epics.rootEpic);
// define store middlewares as an array
export const Middlewares = [
  epicMiddleware,
  LoggerMiddleware.loggerMiddleware,
  promiseMiddleware
];

import { combineReducers } from 'redux'
import { ADD_TO_CART } from '../actions'
import { default as cart, getQuantity, getAddedIds } from './cart'
import { default as products, getProduct } from './products'

export function getCart(state) {
  return state.cart
}

export function getCheckoutError(state) {
  return state.cart.checkoutStatus.error
}

export function isCheckoutPending(state) {
  return state.cart.checkoutStatus.checkoutPending
}

export function getTotal(state) {
  return getAddedIds(state.cart).reduce((total, id) =>
    total + getProduct(state.products, id).price * getQuantity(state.cart, id),
    0
  ).toFixed(2)
}

export function getCartProducts(state) {
  return getAddedIds(state.cart).map(id => ({
    ...getProduct(state.products, id),
    quantity: getQuantity(state.cart, id)
  }))
}

/**
 * shopping cart reducer.
 */
const shoppingCart = combineReducers({
  cart,
  products
})

/**
 * Returns the combined reducer.
 * @param state
 * @param action
 */
export const rootReducer = (state, action) => {
  if(action.type === ADD_TO_CART && state.products.byId[action.productId].inventory <= 0)
    return state;

  return shoppingCart(state, action)
}

import { createStore, applyMiddleware, compose } from 'redux';
import * as reduxLoop from 'redux-loop-symbol-ponyfill';
import {Reducer} from './root-reducer';
import {Epics} from './root-epic';
import { Middlewares } from './root-middleware';
import {composeWithDevTools} from 'redux-devtools-extension';

const enhancers = [
  applyMiddleware(...Middlewares),
  reduxLoop.install()
];

/* Enable redux dev tools only in development.
 * We suggest using the standalone React Native Debugger extension:
 * https://github.com/jhen0409/react-native-debugger
 */
/* tslint-disable no-undef */
const composeEnhancers = composeWithDevTools({});
/* tslint-enable no-undef */

const enhancer = composeEnhancers(...enhancers);

// create the store
export const store = createStore(
  Reducer.mainReducer,
  null,
  enhancer
);

